    <script>
        // Function to modify the style of elements within the iframe
        function modifyIframeContent() {
            // Access the content of the iframe
            var iframe = document.getElementById('looxReviewsFrame');
            var iframeDocument = iframe.contentDocument || iframe.contentWindow.document;

            // Modify the style of elements with class 'summary-text' within the iframe
            var summaryTextElements = iframeDocument.getElementsByClassName('summary-text');
            for (var i = 0; i < summaryTextElements.length; i++) {
                summaryTextElements[i].style.display = 'none';
            }
        }

        // Add an event listener for the load event on the iframe
        var iframe = document.getElementById('looxReviewsFrame');
        iframe.addEventListener('load', modifyIframeContent);
    </script>
    <script>
        try {
            setTimeout(() => {
                var addButton = document.querySelector('.product-form__submit.button span');
                var priceElement = document.querySelector('.price-item.price-item--sale.price-item--last');

                if (addButton && priceElement) {
                    // Extract the price text, remove 'USD' and any excess whitespace, also remove cents
                    var priceText = priceElement.innerHTML.trim().replace(' USD', '').replace(/\.\d\d/, '');

                    // Update the button text to include the price, separated by a ''
                    addButton.innerHTML = addButton.innerHTML.trim() + '  ' + priceText;
                }
            }, 300);
        } catch (e) {
            console.error('Error updating price on button:', e);
        }
    </script>



